sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
   var ga = new GlideAjax('AIDeviceDetails');
   ga.addParam('sysparm_name', 'getDeviceDetails');
   ga.addParam('sysparm_devicename', g_form.getValue('u_device_name_1'));
   ga.getXML(setDeviceDetails);


  function setDeviceDetails(response){
     var answer = response.responseXML.documentElement.getAttribute("answer");
     var obj = JSON.parse(answer);
     g_form.setValue('u_model', obj.model);
     g_form.setValue('u_device_desc', obj.deviceDesc);
     g_form.setValue('u_price', obj.price);
     g_form.setValue('u_warranty_exp', obj.warrantyExp);


  }




}
