sys_script_include.script:
var AIDeviceDetails = Class.create();
AIDeviceDetails.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getDeviceDetails: function(){
      var deviceName = this.getParameter('sysparm_devicename');
      var deviceRec = new GlideRecord('x_1565242_device_m_ai_devices');
      deviceRec.addQuery('sys_id', deviceName);
      deviceRec.query();
      if (deviceRec.next()){
        var deviceObj = {};
		deviceObj.model = ''+ deviceRec.model_number;
		deviceObj.deviceDesc = ''+ deviceRec.short_description;
		deviceObj.price = ''+deviceRec.u_price_2;
        deviceObj.warrantyExp = ''+ deviceRec.warranty_expiration;

		return JSON.stringify(deviceObj);

    }

	},

	type: 'AIDeviceDetails'

});
